public class Especialidad {

        private int id;
	private TipoEspecialidad tipo;
	private RangoEspecialidad rango;

    public Especialidad()
    {
    }
    public Especialidad(TipoEspecialidad tp, RangoEspecialidad rng)
    {
        tipo=tp;
        rango=rng;
    }

    public int getId() {
        return id;
    }

    private void setId(int id) {
        this.id = id;
    }

    public RangoEspecialidad getRango() {
        return rango;
    }

    public void setRango(RangoEspecialidad rango) {
        this.rango = rango;
    }

    public TipoEspecialidad getTipo() {
        return tipo;
    }

    public void setTipo(TipoEspecialidad tipo) {
        this.tipo = tipo;
    }
}
