public class Especialidad {

        private int id;
	private TipoEspecialidad tipo;
	private RangoEspecialidad rango;
        private double precioHoraNormal;

    public Especialidad()
    {
    }
    public Especialidad(TipoEspecialidad tp, RangoEspecialidad rng, double precioHora)
    {
        tipo=tp;
        rango=rng;
        precioHoraNormal=precioHora;
    }
    public Especialidad(TipoEspecialidad tp, RangoEspecialidad rng)
    {
        tipo=tp;
        rango=rng;
    }

    public int getId() {
        return id;
    }

    public void setId(int id) {
        this.id = id;
    }

    public RangoEspecialidad getRango() {
        return rango;
    }

    public void setRango(RangoEspecialidad rango) {
        this.rango = rango;
    }

    public TipoEspecialidad getTipo() {
        return tipo;
    }

    public void setTipo(TipoEspecialidad tipo) {
        this.tipo = tipo;
    }
    public double getPrecioHoraNormal() {
        return precioHoraNormal;
    }

    public void setPrecioHoraNormal(double precioHoraNormal) {
        this.precioHoraNormal = precioHoraNormal;
    }
    
    public String mostrarTipoEspecialidad(){
        if(this.tipo!=null){
            return this.tipo.getNombre();
        }
        return "";
    }
    
    public String mostrarRangoEspecialidad(){
        if(this.rango!=null){
            return this.rango.getNombre();
        }
        return "";
    }    
    
    public String mostrarPrecioPorHora(){
        return "$"+this.precioHoraNormal;
    }        
    
}
